    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        test_menu.setVisible(false);
        question.setVisible(true);
        question.setSize(400, 400);
        question.setDefaultCloseOperation(EXIT_ON_CLOSE);
        System.out.println(jComboBox1.getSelectedItem().toString().split("\\)")[0]);
        tno=Integer.parseInt(jComboBox1.getSelectedItem().toString().split("\\)")[0]);
           try{  
Class.forName("com.mysql.jdbc.Driver");  
Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/test_software","root","");  
Statement stmt=con.createStatement(); 

ResultSet rs;
rs=stmt.executeQuery("select count(*) as c from question where topicno="+tno);
while (rs.next()){
    //System.out.println(rs.getString("quest")+"\t"+rs.getInt("qno"));
    c=rs.getInt("c");
    quest=new String[c];
    op1=new String[c];
    op2=new String[c];
    op3=new String[c];
    op4=new String[c];
    ans=new int[c];
    qno=new int[c];
    cans=new int[c];
    
}
rs=stmt.executeQuery("select * from question where topicno="+tno);
int i=0;
while(rs.next()){
    quest[i]=rs.getString("quest");
    op1[i]=rs.getString("op1");
    op2[i]=rs.getString("op2");
    op3[i]=rs.getString("op3");
    op4[i]=rs.getString("op4");
    qno[i]=rs.getInt("qno");
    cans[i]=rs.getInt("cans");
    
    i++;
}
jTextArea1.setText(quest[0]);
jRadioButton1.setText(op1[0]);
jRadioButton2.setText(op2[0]);
jRadioButton3.setText(op3[0]);
jRadioButton4.setText(op4[0]);
curr=0;
if(curr+1==c) {jButton6.setVisible(true);jButton5.setVisible(false);}
else {jButton5.setVisible(true);jButton6.setVisible(false);}
con.close();
        }catch (Exception e){
        e.printStackTrace();
        }
    }//GEN-LAST:event_jButton3ActionPerformed

