    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        //NEXT BUTTON code 
        // TODO add your handling code here:
        //SAVE CURRENT ANSWER SELECTION IN DATABASE
        ans[curr]=0;
        if (jRadioButton1.isSelected()) ans[curr]=1;
        else if (jRadioButton2.isSelected()) ans[curr]=2;
        else if (jRadioButton3.isSelected()) ans[curr]=3;
        else if (jRadioButton4.isSelected()) ans[curr]=4;
        
        //user answers yet TO be stored in database
        try{  
Class.forName("com.mysql.jdbc.Driver");  
Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/test_software","root","");  
Statement stmt=con.createStatement(); 

stmt.execute("delete from userans where qno="+qno[curr]+" and username='"+uname.getText()+"'");
if (ans[curr]==cans[curr])
stmt.execute("insert into userans values('"+uname.getText()+"',"+qno[curr]+","+ans[curr]+",'C',sysdate())");  
else stmt.execute("insert into userans values('"+uname.getText()+"',"+qno[curr]+","+ans[curr]+",'W',sysdate())");  

 
con.close();
        }catch (Exception e){}
        //SAVE CMPETE MOving to next question
        
        curr++;
        jTextArea1.setText(quest[curr]);
        jRadioButton1.setText(op1[curr]);
        jRadioButton2.setText(op2[curr]);
        jRadioButton3.setText(op3[curr]);
        jRadioButton4.setText(op4[curr]);
        if(curr+1==c) {jButton6.setVisible(true);jButton5.setVisible(false);}
        else {jButton5.setVisible(true);jButton6.setVisible(false);}
    }//GEN-LAST:event_jButton5ActionPerformed

