    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed
        // TODO add your handling code here:
         tno=Integer.parseInt(jComboBox2.getSelectedItem().toString().split("\\)")[0]);
           try{  
Class.forName("com.mysql.jdbc.Driver");  
Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/test_software","root","");  
Statement stmt=con.createStatement(); 

ResultSet rs;
rs=stmt.executeQuery("select count(*) as c from question where topicno="+tno);
while (rs.next()){
    //System.out.println(rs.getString("quest")+"\t"+rs.getInt("qno"));
    c=rs.getInt("c");
    qno=new int[c];
}
rs=stmt.executeQuery("select qno from question where topicno="+tno);
int i=0;
while (rs.next()){
    qno[i++]=rs.getInt("qno");
}
int correct=0,wrong=0, unattempted=0;
for(i=0;i<c;i++)
{
    rs=stmt.executeQuery("select * from userans where qno="+qno[i]+" and username='"+uname.getText()+"'");
    if(rs.next()){
        if (rs.getString("status").equals("C")) ++correct;
        else ++wrong;
    }
    else
        unattempted++;
}
if(unattempted==c)
    jLabel6.setText("TEST NOT YET ATTEMPTED");
else jLabel6.setText("Correct="+correct+", Wrong="+wrong);
con.close();
           }catch(Exception e){}
    }//GEN-LAST:event_jButton7ActionPerformed

